import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginRegister from './pages/login/LoginRegister';
import MainPage from './pages/main/MainPage';
import AddUser from './pages/admin/AddUser';
import UserList from './pages/admin/UserList';
import PatientList from './pages/patients/PatientList';
import HamburgerMenu from './components/HamburgerMenu';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Controllo per vedere se l'utente è autenticato
    const user = localStorage.getItem('user');
    if (user) {
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <Router>
      {isAuthenticated && <HamburgerMenu />}
      <Routes>
        <Route path="/login" element={<LoginRegister />} />
        <Route path="/main" element={isAuthenticated ? <MainPage /> : <Navigate to="/login" />} />
        <Route path="/add-user" element={isAuthenticated ? <AddUser /> : <Navigate to="/login" />} />
        <Route path="/users" element={isAuthenticated ? <UserList /> : <Navigate to="/login" />} />
        <Route path="/" element={<Navigate to="/login" />} />
        <Route path="/patients" element={isAuthenticated ? <PatientList /> : <Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
