import React, { useEffect, useState } from 'react';
import { Container, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper } from '@mui/material';
import { styled } from '@mui/system';
import { useAuth } from '../../contexts/AuthContext';
import HamburgerMenu from '../../components/HamburgerMenu';

const Root = styled('div')({
  display: 'flex',
  flexDirection: 'column',
  height: '100vh',
  backgroundColor: '#f5f5f5',
});

const Content = styled('div')({
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'flex-start',
  alignItems: 'center',
  flexGrow: 1,
  paddingTop: '64px', // spazio per il menu di navigazione
});

const StyledTable = styled(Table)({
  minWidth: 650,
});

const Header = styled('div')({
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  width: '100%',
  marginBottom: 16,
});

function PatientList() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/patients');
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPatients(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPatients();
  }, []);

  if (loading) {
    return <Root><Typography>Loading...</Typography></Root>;
  }

  if (error) {
    return <Root><Typography>Error: {error}</Typography></Root>;
  }

  return (
    <Root>
      <HamburgerMenu /> {/* Menu di navigazione */}
      <Content>
        <Container component={Paper} style={{ padding: 16 }}>
          <Header>
            <Typography variant="h4" component="h1" gutterBottom>
              Patient List
            </Typography>
          </Header>
          <StyledTable>
            <TableHead>
              <TableRow>
                <TableCell>Nominativo</TableCell>
                <TableCell>Et√†</TableCell>
                <TableCell>Altezza</TableCell>
                <TableCell>Peso</TableCell>
                {user.isadmin && <TableCell>Dottore</TableCell>}
              </TableRow>
            </TableHead>
            <TableBody>
              {patients.map((patient) => (
                <TableRow key={patient.uuid}>
                  <TableCell>{patient.nominativo}</TableCell>
                  <TableCell>{patient.eta}</TableCell>
                  <TableCell>{patient.altezza}</TableCell>
                  <TableCell>{patient.peso}</TableCell>
                  {user.isadmin && <TableCell>{patient.doctorid}</TableCell>}
                </TableRow>
              ))}
            </TableBody>
          </StyledTable>
        </Container>
      </Content>
    </Root>
  );
}

export default PatientList;
