// src/pages/patients/PatientList.jsx
import React, { useEffect, useState } from 'react';
import { Container, Typography, Table, TableBody, TableCell, TableHead, TableRow, Paper } from '@mui/material';
import { styled } from '@mui/system';
import { useAuth } from '../../contexts/AuthContext';
import HamburgerMenu from '../../components/HamburgerMenu';
import './PatientList.css';

const Root = styled('div')({
  display: 'flex',
  flexDirection: 'column',
  height: '100vh',
  backgroundColor: '#f5f5f5',
});

const Content = styled('div')({
  display: 'flex',
  justifyContent: 'flex-start',
  alignItems: 'flex-start',
  flexGrow: 1,
  padding: 16,
});

const StyledTable = styled(Table)({
  minWidth: 650,
});

function PatientList() {
  const [patients, setPatients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { user } = useAuth();

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const endpoint = user.isAdmin ? '/api/patients' : `/api/patients/assigned?doctorid=${user.uuid}`;
        const response = await fetch(`http://localhost:5000${endpoint}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setPatients(data);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
    fetchPatients();
  }, [user.isAdmin, user.uuid]);

  if (loading) {
    return <Root><Typography>Loading...</Typography></Root>;
  }

  if (error) {
    return <Root><Typography>Error: {error}</Typography></Root>;
  }

  return (
    <Root>
      <HamburgerMenu />
      <Content>
        <Container component={Paper}>
          <StyledTable>
            <TableHead>
              <TableRow>
                <TableCell className="table-header">Nominativo</TableCell>
                <TableCell className="table-header">Età</TableCell>
                <TableCell className="table-header">Altezza</TableCell>
                <TableCell className="table-header">Peso</TableCell>
                {user.isAdmin && <TableCell className="table-header">Dottore</TableCell>}
              </TableRow>
            </TableHead>
            <TableBody>
              {patients.map((patient) => (
                <TableRow key={patient.uuid}>
                  <TableCell>{patient.nominativo}</TableCell>
                  <TableCell>{patient.eta}</TableCell>
                  <TableCell>{patient.altezza}</TableCell>
                  <TableCell>{patient.peso}</TableCell>
                  {user.isAdmin && <TableCell>{patient.doctor_name}</TableCell>}
                </TableRow>
              ))}
            </TableBody>
          </StyledTable>
        </Container>
      </Content>
    </Root>
  );
}

export default PatientList;
