import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogContentText,
  DialogActions,
  Button,
  Typography,
  List,
  ListItem,
  ListItemText,
} from '@mui/material';

const PatientInfo = ({ open, onClose, patient }) => {
  const measurements = [
    { date: '2024-06-20', value: '120/80' },
    { date: '2024-06-21', value: '125/85' },
  ];

  const treatments = [
    { date: '2024-06-20', description: 'Antibiotics' },
    { date: '2024-06-21', description: 'Physical Therapy' },
  ];

  const pathologies = [
    { name: 'Hypertension' },
    { name: 'Diabetes' },
  ];

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>Cartella Clinica di {patient.nominativo}</DialogTitle>
      <DialogContent>
        <Typography variant="h6">Informazioni del Paziente</Typography>
        <DialogContentText>Nome: {patient.nominativo}</DialogContentText>
        <DialogContentText>Et√†: {patient.eta}</DialogContentText>
        <DialogContentText>Altezza: {patient.altezza}</DialogContentText>
        <DialogContentText>Peso: {patient.peso}</DialogContentText>

        <Typography variant="h6" style={{ marginTop: '1em' }}>Misurazioni</Typography>
        <List>
          {measurements.map((measurement, index) => (
            <ListItem key={index}>
              <ListItemText primary={`${measurement.date}: ${measurement.value}`} />
            </ListItem>
          ))}
        </List>

        <Typography variant="h6" style={{ marginTop: '1em' }}>Trattamenti</Typography>
        <List>
          {treatments.map((treatment, index) => (
            <ListItem key={index}>
              <ListItemText primary={`${treatment.date}: ${treatment.description}`} />
            </ListItem>
          ))}
        </List>

        <Typography variant="h6" style={{ marginTop: '1em' }}>Patologie</Typography>
        <List>
          {pathologies.map((pathology, index) => (
            <ListItem key={index}>
              <ListItemText primary={pathology.name} />
            </ListItem>
          ))}
        </List>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="primary">Chiudi</Button>
      </DialogActions>
    </Dialog>
  );
};

export default PatientInfo;
